// CALCULATE DOWNLOAD SPEED OF SPECIFIC ITEMS
function download_time($speed, $type){
  
  $items = array("MP3"=>"6", "CD"=>"700", "DVD"=>"4800");
  
  $data_per_second = ($speed / 8);
  $result = round($items[$type] / $data_per_second);
  
  return $result;
}

